import { FC } from "react";
import clsx from "clsx";
import { motion } from "motion/react";

export interface ProgressProps {
    /**
     * Current value of progress
     */
    now: number;
    /**
     * Minimum value progress can begin from
     */
    min?: number;
    /**
     * Maximum value progress can reach
     */
    max?: number;
    /**
     * Show label that represents visual percentage. EG. 60%
     */
    isLabel?: boolean;
    /**
     * Optional. Default is `primary`.
     */
    color?: "primary" | "success" | "danger";
    /**
     * Optional. Default is `md`.
     */
    size?: "xxs" | "xs" | "sm" | "md" | "lg";
    /**
     * Optional. Disable Animation. Default is `false`
     */
    disableAnimation?: boolean;
    /**
     * Extra Class Name
     */
    className?: string;
}

const ProgressBar: FC<ProgressProps> = ({
    className,
    now,
    min,
    max,
    isLabel,
    color,
    size,
    disableAnimation,
    ...restProps
}) => {
    const colorClass = [
        color === "primary" && "tw:bg-primary",
        color === "success" && "tw:bg-success",
        color === "danger" && "tw:bg-danger",
    ];
    const sizeClass = [
        size === "xxs" && "tw:h-0.5",
        size === "xs" && "tw:h-1.5",
        size === "sm" && "tw:h-2.5",
        size === "md" && "tw:h-3.8",
        size === "lg" && "tw:h-5",
    ];
    return (
        <div className={clsx("progress tw:rounded-sm tw:bg-gray-500", className)} {...restProps}>
            <motion.div
                role="progressbar"
                aria-valuenow={now}
                aria-valuemin={min}
                aria-valuemax={max}
                initial={{
                    width: disableAnimation ? `${now}%` : 0,
                }}
                whileInView={{ width: `${now}%` }}
                viewport={{ once: true, amount: 0.4 }}
                transition={{
                    duration: 1,
                    type: "tween",
                    delay: 0.5,
                }}
                className={clsx(
                    "progress-bar tw:flex tw:items-center tw:justify-center tw:rounded-l-sm",
                    color && colorClass,
                    size && sizeClass
                )}
            >
                <span className={!isLabel ? "tw:sr-only" : "tw:text-[11px] tw:text-white"}>
                    {now}%
                </span>
            </motion.div>
        </div>
    );
};

ProgressBar.defaultProps = {
    now: 40,
    min: 0,
    max: 100,
    color: "primary",
    size: "xs",
    disableAnimation: false,
};

ProgressBar.displayName = "ProgressBar";

export default ProgressBar;

import clsx from "clsx";

type TProps = {
    children: React.ReactNode;
    className?: string;
    color?: "light" | "white" | "dark";
    variant?: "contained" | "outlined" | "texted";
    size?: "xs" | "sm" | "md" | "lg" | "xl";
    shape?: "square" | "rounded" | "circle";
    hover?: boolean;
    href: string;
    label: string;
    tooltip?: boolean;
    onClick?: (e: React.MouseEvent<HTMLAnchorElement>) => void;
};

const SocialLink = ({
    children,
    className,
    href,
    label,
    color,
    variant,
    size,
    shape,
    hover,
    tooltip,
    onClick,
}: TProps) => {
    // texted variant
    const textedWhite = color === "white" && hover && "tw:text-white/50 tw:hover:text-white";
    const textedLight = color === "light" && hover && "tw:text-body/50 tw:hover:text-primary";
    const textedDark = color === "dark" && hover && "tw:text-body tw:hover:text-primary";
    const textedXl = size === "xl" && "tw:text-2xl";
    const textedLg = size === "lg" && "tw:text-lg";

    // Toopltip
    const tooltipBeforeClass =
        "tw:before:absolute tw:before:content-[''] tw:before:border-[7px] tw:before:border-transparent tw:before:border-t-primary tw:before:bottom-full before:left-[calc(50%_-_7px) tw:before:transition-all tw:before:duration-300 tw:before:ease-[cubic-bezier(.71,1.7,.77,1.24)] tw:before:pointer-events-none tw:before:z-20 tw:before:mb-[-13px] tw:before:invisible tw:before:opacity-0";
    const tooltipAfterClass =
        "tw:after:absolute tw:after:content-[attr(aria-label)] tw:after:bg-primary tw:after:text-white tw:after:py-2 tw:after:px-2.5 tw:after:leading-none tw:after:whitespace-nowrap tw:after:rounded tw:after:shadow-xs tw:after:shadow-black/30 tw:after:bottom-full tw:after:left-1/2 tw:after:-translate-x-1/2 tw:after:transition-all tw:after:duration-300 tw:after:ease-[cubic-bezier(.71,1.7,.77,1.24)] tw:after:pointer-events-none tw:after:z-10 tw:after:invisible tw:after:opacity-0";
    const tooltipHoverClass =
        "tw:hover:before:visible tw:hover:before:opacity-100 tw:hover:before:delay-100 tw:hover:before:-translate-y-2 tw:hover:after:visible tw:hover:after:opacity-100 tw:hover:after:delay-100 tw:hover:after:-translate-y-2";

    return (
        <a
            className={clsx(
                "social-link tw:relative tw:leading-none",
                variant === "texted" && [textedWhite, textedLight, textedDark, textedXl, textedLg],
                variant !== "texted" && [
                    "tw:text-center",
                    size === "md" && "tw:h-10 tw:w-10 tw:leading-10",
                    color === "light" &&
                        "tw:border-gray-550 tw:hover:border-primary tw:hover:bg-primary tw:hover:text-white",
                ],
                variant === "outlined" && "tw:border tw:bg-transparent",
                shape === "rounded" && "tw:rounded",
                shape === "circle" && "tw:rounded-full",
                tooltip && [tooltipBeforeClass, tooltipAfterClass, tooltipHoverClass],
                className
            )}
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            aria-label={label}
            onClick={onClick}
        >
            {children}
        </a>
    );
};

SocialLink.displayName = "SocialLink";

export default SocialLink;

import { forwardRef } from "react";
import cn from "clsx";
import SwiperCore, { Navigation, Pagination, Autoplay, A11y } from "swiper";
// eslint-disable-next-line import/no-unresolved
import { Swiper, SwiperSlide } from "swiper/react";

type TOptions = {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    modules?: any[];
    slidesPerView?: number;
    spaceBetween?: number;
    watchSlidesProgress?: boolean;
    autoHeight?: boolean;
    speed?: number;
    centeredSlides?: boolean;
    effect?: "slide" | "fade" | "cube" | "coverflow" | "flip" | "creative" | "cards";
    navigation?:
        | boolean
        | {
              prevEl: string;
              nextEl: string;
          };
    pagination?:
        | boolean
        | {
              clickable: boolean;
          };
    loop?: boolean;
    autoplay?:
        | boolean
        | {
              delay?: number;
              disableOnInteraction?: boolean;
          };
    breakpoints?: {
        [x: number]: {
            [x: string]: number | string;
        };
    };
    onSlideChange?: (swiper: SwiperCore) => void;
    onSlideChangeTransitionStart?: (swiper: SwiperCore) => void;
    onSlideChangeTransitionEnd?: (swiper: SwiperCore) => void;
};

type TProps = {
    options?: TOptions;
    prevIcon?: string;
    nextIcon?: string;
    navStyle?: 1 | 2;
    navPosition?: "top" | "bottom";
    shadowSize?: "small" | "medium" | "large";
    dotStyle?: 1 | 2;
    dotPosition?: "top" | "bottom";
    children: React.ReactNode;
    className?: string;
    navClass?: string;
    onSlideChange?: (swiper: SwiperCore) => void;
    onSlideChangeTransitionStart?: (swiper: SwiperCore) => void;
    onSlideChangeTransitionEnd?: (swiper: SwiperCore) => void;
};

const SwiperSlider = forwardRef<HTMLDivElement, TProps>(
    (
        {
            options,
            prevIcon,
            nextIcon,
            navStyle,
            navPosition,
            shadowSize,
            dotStyle,
            dotPosition,
            children,
            className,
            navClass,
        },
        ref
    ) => {
        const modules = options?.modules !== undefined ? options.modules : [];
        const prevClass = `prev-${navClass || "swiper-nav"}`;
        const nextClass = `next-${navClass || "swiper-nav"}`;
        const sliderOptions: TOptions = {
            slidesPerView: 3,
            spaceBetween: 30,
            loop: false,
            autoplay: {
                delay: 2500,
                disableOnInteraction: false,
            },
            watchSlidesProgress: true,
            autoHeight: true,
            breakpoints: {},
            ...options,
            // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
            modules: [Navigation, Pagination, A11y, Autoplay, ...modules],
            navigation: options?.navigation
                ? {
                      prevEl: `.${prevClass}`,
                      nextEl: `.${nextClass}`,
                  }
                : false,
            pagination: options?.pagination
                ? {
                      clickable: true,
                  }
                : false,
        };

        return (
            <div
                className={cn(
                    className,
                    "swiper-wrap tw:relative",
                    navStyle && `nav-style-${navStyle}`,
                    navPosition && `nav-position-${navPosition}`,
                    dotStyle && `dot-style-${dotStyle}`,
                    dotPosition && `dot-position-${dotPosition}`,
                    shadowSize && `shadow-size-${shadowSize}`
                )}
                ref={ref}
            >
                <Swiper {...sliderOptions}>{children}</Swiper>

                {sliderOptions?.navigation && (
                    <>
                        <button type="button" className={`swiper-btn swiper-btn-prev ${prevClass}`}>
                            <i className={cn(prevIcon, "icon")} />
                        </button>
                        <button type="button" className={`swiper-btn swiper-btn-next ${nextClass}`}>
                            <i className={cn(nextIcon, "icon")} />
                        </button>
                    </>
                )}
            </div>
        );
    }
);

export { SwiperSlide };

SwiperSlider.defaultProps = {
    prevIcon: "fal fa-angle-left",
    nextIcon: "fal fa-angle-right",
    navStyle: 1 as const,
    dotStyle: 1 as const,
};

export default SwiperSlider;

import { Children, isValidElement, cloneElement, ReactNode, ReactElement } from "react";
import clsx from "clsx";

interface IProps {
    children: ReactNode;
    striped?: boolean;
    bordered?: boolean;
    className?: string;
}

// Enhanced type for child props
interface TChildProps {
    className?: string;
    children?: ReactNode;
    [key: string]: unknown;
}

type TableSectionType = "thead" | "tbody" | "tfoot";

const BasicTable = ({ children, className, striped, bordered }: IProps) => {
    const baseTHClass = "tw:py-7.5 tw:px-7.5 tw:text-center";
    const baseTDClass = "tw:py-2.5 tw:px-5 tw:text-center";
    const borderedClass =
        bordered && "tw:border tw:border-black/8 tw:border-l-0 tw:first:border-l";

    const iterateOverChildren = (reactChildren: ReactNode, parent: TableSectionType): ReactNode => {
        return Children.map(reactChildren, (child) => {
            if (!isValidElement(child)) return child;

            const childProps = child.props as TChildProps;
            const childType = child.type as keyof JSX.IntrinsicElements;

            const baseProps = {
                ...childProps,
            };

            if (parent === "thead") {
                return cloneElement(child as ReactElement<TChildProps>, {
                    ...baseProps,
                    className: clsx(
                        (childType === "th" || childType === "td") && [baseTHClass, borderedClass],
                        childProps.className
                    ),
                    children: iterateOverChildren(childProps.children, parent),
                });
            }

            if (parent === "tbody" || parent === "tfoot") {
                return cloneElement(child as ReactElement<TChildProps>, {
                    ...baseProps,
                    className: clsx(
                        (childType === "th" || childType === "td") && [baseTDClass, borderedClass],
                        childType === "th" && "!text-heading font-medium",
                        childType === "tr" && [striped && "tw:even:bg-black/2"],
                        childProps.className
                    ),
                    children: iterateOverChildren(childProps.children, parent),
                });
            }
            return null;
        });
    };

    const renderChildren = Children.map(children, (child) => {
        if (!isValidElement(child)) return child;

        const childProps = child.props as TChildProps;
        const childType = child.type as keyof JSX.IntrinsicElements;

        const baseProps = {
            ...childProps,
        };

        if (childType === "thead" || childType === "tbody" || childType === "tfoot") {
            return cloneElement(child as ReactElement<TChildProps>, {
                ...baseProps,
                children: iterateOverChildren(childProps.children, childType),
            });
        }

        return cloneElement(child as ReactElement<TChildProps>, {
            ...baseProps,
            children: childProps.children,
        });
    });

    return (
        <div className="table-responsive">
            <table className={clsx("table tw:w-full", className)}>{renderChildren}</table>
        </div>
    );
};

export default BasicTable;

