import { VWCContributor, GithubRepo, GithubContributor, GithubUser } from "@utils/types";
import { gitAPI } from "./git-api-client";

export const getProjectContributors = async (
    owner: string,
    repo: string,
    top: number = 4
): Promise<VWCContributor[]> => {
    const topContributors = await getGithubRepoContributors(owner, repo, top);
    const projectContributors = Promise.all(
        topContributors.map(async (contributor) => {
            const response = await gitAPI.get(`/users/${contributor.login}`);
            if (response.status == 200) {
                const user = response.data as GithubUser;
                return {
                    ...contributor,
                    ...user,
                };
            } else {
                if ("error" in response) {
                    throw new Error(
                        `Error fetching user data for ${contributor.login}\nStatus code: ${response.status}\nError: ${response.error}`
                    );
                }
                throw new Error(
                    `Error fetching user data for ${contributor.login}\nStatus code: ${response.status}`
                );
            }
        })
    );
    return await projectContributors;
};

export const getGithubRepo = async (owner: string, repo: string): Promise<GithubRepo> => {
    const response = await gitAPI.get(`/repos/${owner}/${repo}`);
    if (response.status == 200) {
        return response.data as GithubRepo;
    } else {
        if ("error" in response) {
            throw new Error(
                `Error fetching repo data for ${owner}/${repo}\nStatus code: ${response.status}\nError: ${response.error}`
            );
        }
        throw new Error(
            `Error fetching repo data for ${owner}/${repo}\nStatus code: ${response.status}`
        );
    }
};

export const getGithubRepoContributors = async (
    owner: string,
    repo: string,
    top: number = 4
): Promise<GithubContributor[]> => {
    const response = await gitAPI.get(`/repos/${owner}/${repo}/contributors`);
    if (response.status == 200) {
        return (response.data as GithubContributor[]).slice(0, top);
    } else {
        if ("error" in response) {
            throw new Error(
                `Error fetching contributor data for ${owner}/${repo}\nStatus code: ${response.status}\nError: ${response.error}`
            );
        }
        throw new Error(
            `Error fetching contributor data for ${owner}/${repo}\nStatus code: ${response.status}`
        );
    }
};

import path from "path";
import { VWCProject, VWCProjectDetails } from "@utils/types";
import fs from "fs";
import { getSlugs } from "./util";
import { getGithubRepo, getProjectContributors } from "./github";

const projectDirectory = path.join(process.cwd(), "src/data/projects");

export function getProjectBySlug(slug: string): VWCProjectDetails {
    const fullPath = path.join(projectDirectory, slug);
    return JSON.parse(fs.readFileSync(fullPath, "utf8"));
}

export const getAllProjects = (): VWCProjectDetails[] => {
    const slugs = getSlugs(projectDirectory);
    return slugs.map((slug) => getProjectBySlug(slug));
};

export const getProjectData = async (): Promise<VWCProject[]> => {
    const projects = getAllProjects();
    const data = Promise.all(
        projects.map(async (project) => {
            const repo = await getGithubRepo(project.owner, project.repo);
            const contributors = await getProjectContributors(project.owner, project.repo);
            const data = {
                details: project,
                repo: {
                    ...repo,
                    contributors: contributors,
                },
            };
            return data;
        })
    );
    // Sort projects by index
    return (await data).sort((a, b) => a.details.index - b.details.index);
};

import axios from "axios";
import { Request, Response } from "express";
import { checkParams } from "./api-helpers";

// Define the ParsedBody interface to type-check the request body
interface ParsedBody {
    firstName?: string;
    lastName?: string;
    email?: string;
    city?: string;
    state?: string;
    zipCode?: number;
    country?: string;
    branchOfService?: string;
    yearJoined?: number;
    yearSeparated?: number;
    hasAttendedPreviousCourse?: boolean;
    previousCourses?: string;
    willAttendAnotherCourse?: boolean;
    otherCourses?: string;
    linkedInAccountName?: string;
    githubAccountName?: string;
    preworkLink?: string;
    preworkRepo?: string;
}

export default async function handler(req: Request, res: Response) {
    try {
        const parsedBody = req.body as ParsedBody; // Ensure body matches ParsedBody interface
        const requiredParams: (keyof ParsedBody)[] = [
            "firstName",
            "lastName",
            "email",
            "city",
            "state",
            "zipCode",
            "country",
            "branchOfService",
            "yearJoined",
            "yearSeparated",
            "hasAttendedPreviousCourse",
            "willAttendAnotherCourse",
            "linkedInAccountName",
            "githubAccountName",
            "preworkLink",
            "preworkRepo",
        ];

        // Validate required fields in the parsed body
        const hasErrors = checkParams(parsedBody, requiredParams);

        if (hasErrors) {
            return res.status(422).json({ error: "Missing or incorrect required property" });
        }

        // Construct the text message to be sent
        const items = [
            `First Name: \`${parsedBody.firstName ?? ""}\``,
            `Last Name: \`${parsedBody.lastName ?? ""}\``,
            `Email: \`${parsedBody.email ?? ""}\``,
            `City: \`${parsedBody.city ?? ""}\``,
            `State: \`${parsedBody.state ?? ""}\``,
            `Zip Code: \`${parsedBody.zipCode ?? ""}\``,
            `Country: \`${parsedBody.country ?? ""}\``,
            `Branch of Service: \`${parsedBody.branchOfService ?? ""}\``,
            `Year Joined: \`${parsedBody.yearJoined ?? ""}\``,
            `Year Separated: \`${parsedBody.yearSeparated ?? ""}\``,
            `Has attended previous bootcamp/programs: \`${
                parsedBody.hasAttendedPreviousCourse ? "Yes" : "No"
            }\``,
            `Will do other courses/programs concurrently: \`${
                parsedBody.willAttendAnotherCourse ? "Yes" : "No"
            }\``,
            `LinkedIn Account Name: \`${parsedBody.linkedInAccountName ?? ""}\``,
            `GitHub Account Name: \`${parsedBody.githubAccountName ?? ""}\``,
            `Prework Link: \`${parsedBody.preworkLink ?? ""}\``,
            `Prework Repository: \`${parsedBody.preworkRepo ?? ""}\``,
        ];

        if (parsedBody.willAttendAnotherCourse && parsedBody.otherCourses !== "") {
            items.splice(12, 0, `\`\`\`${parsedBody.otherCourses}\`\`\``);
        }

        if (parsedBody.hasAttendedPreviousCourse && parsedBody.previousCourses !== "") {
            items.splice(11, 0, `\`\`\`${parsedBody.previousCourses}\`\`\``);
        }

        const text = items.join("\n");

        // Send the payload to the configured Slack webhook URL
        await axios.post(
            `https://hooks.slack.com/services/${process.env.APPLY_WEBHOOK_ID ?? ""}`,
            JSON.stringify({ text })
        );

        // Respond with success message
        return res.status(200).json({ message: "SUCCESS" });
    } catch (err) {
        // Log the error for debugging and respond with an error message
        // console.error("Failed to post to #mentor channel:", err);
        return res.status(500).json({ message: "Failed to post to #apply channel" });
    }
}

import NextAuth from "next-auth";
import GithubProvider from "next-auth/providers/github";
import type { AuthOptions } from "next-auth";

// Utility function to handle GitHub API requests with timeout
const fetchWithTimeout = async (
    url: string,
    options: RequestInit,
    timeout = 5000
): Promise<Response> => {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
        const response = await fetch(url, {
            ...options,
            signal: controller.signal,
        });
        clearTimeout(timeoutId);
        return response;
    } catch (error) {
        clearTimeout(timeoutId);
        throw error;
    }
};

export const authOptions: AuthOptions = {
    providers: [
        GithubProvider({
            clientId: process.env.GITHUB_CLIENT_ID as string,
            clientSecret: process.env.GITHUB_CLIENT_SECRET as string,
            authorization: {
                params: {
                    scope: "read:org",
                },
            },
        }),
    ],
    callbacks: {
        async signIn({ account }) {
            if (account?.provider === "github" && account.access_token) {
                try {
                    // Add timeout protection to the GitHub API call
                    const res = await fetchWithTimeout(
                        "https://api.github.com/user/orgs",
                        {
                            headers: {
                                Accept: "application/vnd.github.v3+json",
                                Authorization: `Bearer ${account.access_token}`,
                                "User-Agent": "NextAuth.js",
                            },
                        },
                        5000 // 5 second timeout
                    );

                    // Handle rate limiting
                    if (res.status === 403) {
                        console.error("GitHub API rate limit exceeded");
                        return false;
                    }

                    if (!res.ok) {
                        console.error(`GitHub API error: ${res.status}`);
                        return false;
                    }

                    const orgs = await res.json();

                    if (!Array.isArray(orgs)) {
                        console.error("Invalid response from GitHub API");
                        return false;
                    }

                    const githubOrg = process.env.GITHUB_ORG;
                    if (!githubOrg) {
                        console.error("GITHUB_ORG environment variable not set");
                        return false;
                    }

                    const isMember = orgs.some((org) => org.login === githubOrg);

                    if (!isMember) {
                        console.error("User is not a member of the required organization");
                        return false;
                    }

                    return true;
                } catch (error) {
                    if (error instanceof Error) {
                        if (error.name === "AbortError") {
                            console.error("GitHub API request timed out");
                        } else {
                            console.error("Error checking GitHub organization membership:", error);
                        }
                    }
                    return false;
                }
            }

            return true;
        },
        async session({ session, token }) {
            try {
                if (session.user) {
                    session.user.id = token.id as string;
                }
                return session;
            } catch (error) {
                console.error("Error in session callback:", error);
                return session;
            }
        },
        async jwt({ token, user, account }) {
            try {
                if (account && user) {
                    token.id = user.id;
                }
                return token;
            } catch (error) {
                console.error("Error in JWT callback:", error);
                return token;
            }
        },
    },
    pages: {
        error: "/auth/error", // Add this if you want to handle auth errors with a custom page
    },
    secret: process.env.NEXTAUTH_SECRET,
    debug: process.env.NODE_ENV === "development",
};

// Type augmentation for next-auth
declare module "next-auth" {
    interface Session {
        user: {
            id: string;
            name?: string | null;
            email?: string | null;
            image?: string | null;
        };
    }
}

export default NextAuth(authOptions);

import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import Link from "next/link";
import type { NextPage } from "next";
import Layout from "@layout/layout-01";

type ErrorType = {
    [key: string]: {
        title: string;
        message: string;
        action: string;
    };
};

const errorMessages: ErrorType = {
    default: {
        title: "Authentication Error",
        message: "An unexpected error occurred during the authentication process.",
        action: "Please try signing in again.",
    },
    configuration: {
        title: "Server Configuration Error",
        message: "There is a problem with the server configuration.",
        action: "Please contact support for assistance.",
    },
    accessdenied: {
        title: "Access Denied",
        message:
            "You must be a member of the required GitHub organization to access this application.",
        action: "Please request access from your organization administrator.",
    },
    verification: {
        title: "Account Verification Required",
        message: "Your account requires verification before continuing.",
        action: "Please check your email for verification instructions.",
    },
    signin: {
        title: "Sign In Error",
        message: "The sign in attempt was unsuccessful.",
        action: "Please try again or use a different method to sign in.",
    },
    callback: {
        title: "Callback Error",
        message: "There was a problem with the authentication callback.",
        action: "Please try signing in again. If the problem persists, clear your browser cookies.",
    },
    oauthsignin: {
        title: "GitHub Sign In Error",
        message: "Unable to initiate GitHub sign in process.",
        action: "Please try again or check if GitHub is accessible.",
    },
    oauthcallback: {
        title: "GitHub Callback Error",
        message: "There was a problem processing the GitHub authentication.",
        action: "Please try signing in again or ensure you've granted the required permissions.",
    },
};

type PageWithLayout = NextPage & {
    Layout?: typeof Layout;
};

const AuthError: PageWithLayout = () => {
    const router = useRouter();
    const [error, setError] = useState(errorMessages.default);
    const [countdown, setCountdown] = useState(10);

    useEffect(() => {
        const errorType = router.query.error as string;
        if (errorType && errorMessages[errorType]) {
            setError(errorMessages[errorType]);
        }
    }, [router.query]);

    useEffect(() => {
        const timer = setInterval(() => {
            setCountdown((prev) => {
                if (prev <= 1) {
                    clearInterval(timer);
                    router.push("/").catch(console.error);
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, [router]);

