import axios, { AxiosRequestConfig } from "axios";
import { NextApiRequest, NextApiResponse } from "next";
import { checkParams, checkLength, contactErrors } from "./api-helpers";

interface ParsedBody {
    name?: string;
    email?: string;
    phone?: string;
    subject?: string;
    message?: string;
    [key: string]: string | undefined;
}

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
    const parsedBody: ParsedBody = req.body as ParsedBody;
    const { name, email, phone, message } = parsedBody;
    const requiredParams: string[] = ["email", "message"];

    const hasErrors: boolean = checkParams(parsedBody, requiredParams);
    const isPossiblySpam: boolean = checkLength(message ?? "");

    if (hasErrors) {
        return res.status(422).json({
            error: contactErrors.missingOrRequired,
        });
    }

    if (isPossiblySpam) {
        return res.status(400).json({
            error: contactErrors.tooShort,
        });
    }

    const safeMessage: string = message ?? "No message provided.";

    const text: string = [
        `Name: \`${name ?? "Sent from footer form."}\``,
        `\nEmail: \`${email ?? "Not provided."}\``,
        `\nPhone: \`${phone ?? "Not provided."}\``,
        `\nMessage: \n\`\`\`${safeMessage}\`\`\``,
    ].join("");

    const payload: string = JSON.stringify({ text });

    const axiosConfig: AxiosRequestConfig = {
        method: "POST",
        baseURL: "https://hooks.slack.com",
        url: `/services/${(process.env.CONTACT_WEBHOOK_ID as string) ?? ""}`,
        data: payload,
    };

    try {
        await axios(axiosConfig);
        return res.status(200).json({ message: "SUCCESS" });
    } catch (err: unknown) {
        if (err instanceof Error) {
            return res.status(500).json({
                message: `Failed post to #contact channel: ${err.message}`,
            });
        }
        return res.status(500).json({
            message: "An unexpected error occurred.",
        });
    }
}

import { NextApiRequest, NextApiResponse } from "next";

const MAILCHIMP_API_SERVER = "https://us4.api.mailchimp.com/3.0/lists";
const MAILCHIMP_LIST_ID = process.env?.MAILCHIMP_LIST_ID ?? "";
const MAILCHIMP_API_KEY = process.env?.MAILCHIMP_API_KEY ?? "";

type RequestData = {
    newsletter_email: string;
};
// Response structure for a successful operation
interface MailchimpSuccessResponse {
    id: string;
    status: string;
}

// Response structure for an error in the Mailchimp API
interface MailchimpErrorResponse {
    status: number;
    title: string;
    detail: string;
    message: string;
}

// Response structure for a Mailchimp member
interface MailchimpMember {
    email_address: string;
    status: string;
}

// Response structure for a Mailchimp campaign
interface MailchimpCampaign {
    id: string;
    type: string;
}

type Mailchimp =
    | MailchimpSuccessResponse
    | MailchimpErrorResponse
    | MailchimpMember
    | MailchimpCampaign;

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
    if (req.method !== "POST") {
        return res.status(405).json({ error: "Method Not Allowed" });
    }
    let requestData: RequestData;

    try {
        requestData = JSON.parse(req.body as string) as RequestData;
    } catch (error) {
        return res.status(400).json({ error: "Invalid JSON payload" });
    }

    const { newsletter_email: email } = requestData;

    // Validate the email address
    if (!email || typeof email !== "string") {
        return res.status(400).json({ error: "Invalid email address" });
    }

    // Subscribe the email using Mailchimp API
    try {
        const response = await fetch(`${MAILCHIMP_API_SERVER}/${MAILCHIMP_LIST_ID}/members`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Basic ${Buffer.from(`apikey:${MAILCHIMP_API_KEY}`).toString(
                    "base64"
                )}`,
            },
            body: JSON.stringify({
                // email_address: email,
                email_address: email,
                status: "subscribed",
            }),
        });

        let data: Mailchimp;

        try {
            data = (await response.json()) as Mailchimp;
        } catch (error) {
            // Handle JSON parsing error
            return res.status(500).json({ error: "Internal Server Error" });
        }

        if (response.ok) {
            // Successful subscription
            return res.status(200).json({ message: "Email subscribed successfully", ok: true });
        }

        // Error from Mailchimp API
        if (data) {
            const errorRes = data as MailchimpErrorResponse;
            return res.status(errorRes.status).json({
                error: errorRes.title,
                ok: false,
                detail: errorRes.detail,
            });
        }

        // Other error
        return res.status(500).json({ error: "Internal Server Error" });
    } catch (error: unknown) {
        const errorRes = error as MailchimpErrorResponse;
        // Error handling
        if (typeof errorRes === "object" && errorRes !== null && "status" in errorRes) {
            // Error from Mailchimp API
            return res.status(errorRes.status).json({ error: errorRes.message });
        }
        // Other error
        return res.status(500).json({ error: "Internal Server Error" });
    }
}

import React, { useEffect, useCallback, useState } from "react";
import { useRouter } from "next/router";
import { useSession, signIn } from "next-auth/react";
import Spinner from "@ui/spinner";
import { useMount } from "@hooks";
import type { GetStaticProps, NextPage } from "next";
import Layout from "@layout/layout-01";

interface LoginProps {
    layout?: {
        headerShadow: boolean;
        headerFluid: boolean;
        footerMode: string;
    };
}

type PageWithLayout = NextPage<LoginProps> & {
    Layout?: typeof Layout;
};

const Login: PageWithLayout = () => {
    const mounted = useMount();
    const { status, data: session } = useSession();
    const router = useRouter();
    const [errorMessage, setErrorMessage] = useState<string | null>(null);
    const [isRedirecting, setIsRedirecting] = useState(false);

    useEffect(() => {
        if (status === "authenticated" && session && !isRedirecting) {
            setIsRedirecting(true);
            router.replace("/profile").catch((error) => {
                console.error("Redirect error:", error);
                setErrorMessage("Failed to redirect to profile. Please try refreshing the page.");
                setIsRedirecting(false);
            });
        }
    }, [status, session, router, isRedirecting]);

    const handleSignIn = useCallback(async () => {
        try {
            setErrorMessage(null);
            const result = await signIn("github", {
                redirect: false,
            });

            if (result?.error) {
                setErrorMessage("Failed to sign in with GitHub. Please try again.");
            }
        } catch (error) {
            console.error("Sign in error:", error);
            setErrorMessage("An unexpected error occurred. Please try again.");
        }
    }, []);

    if (!mounted || status === "loading") {
        return (
            <div className="tw:fixed tw:top-0 tw:z-50 tw:flex tw:h-screen tw:w-screen tw:items-center tw:justify-center tw:bg-white">
                <Spinner />
            </div>
        );
    }

    if (status === "unauthenticated") {
        return (
            <div className="tw:flex tw:min-h-screen tw:items-center tw:justify-center tw:bg-secondary">
                <div className="tw:w-full tw:max-w-md tw:overflow-hidden tw:rounded-lg tw:bg-white tw:shadow-lg">
                    <div className="tw:space-y-3 tw:p-8">
                        <h1 className="tw:text-center tw:text-2xl tw:font-bold tw:text-secondary">
                            Retool. Retrain. Relaunch.
                        </h1>
                        <p className="tw:text-center tw:text-secondary">
                            Sign in to continue your journey with #VetsWhoCode
                        </p>
                        {errorMessage && (
                            <div className="tw:rounded tw:bg-red-50 tw:p-3 tw:text-sm tw:text-red-600">
                                {errorMessage}
                            </div>
                        )}
                    </div>
                    <div className="tw:p-6">
                        <button
                            type="button"
                            onClick={handleSignIn}
                            className="tw:flex tw:w-full tw:items-center tw:justify-center tw:gap-2 tw:rounded-md tw:bg-primary tw:px-4 tw:py-3 tw:text-sm tw:font-medium tw:text-white tw:transition-colors tw:hover:opacity-90"
                            disabled={isRedirecting}
                        >
                            <i className="fab fa-github" />
                            Sign in with GitHub
                        </button>
                    </div>
                    <div className="tw:px-8 tw:pb-8">
                        <p className="tw:text-center tw:text-sm tw:text-secondary">
                            By clicking continue, you agree to our{" "}
                            <a
                                href="/terms"
                                className="tw:text-primary tw:underline tw:underline-offset-4 tw:hover:opacity-80"
                            >
                                Terms of Service
                            </a>{" "}
                            and{" "}
                            <a
                                href="/privacy"
                                className="tw:text-primary tw:underline tw:underline-offset-4 tw:hover:opacity-80"
                            >
                                Privacy Policy
                            </a>
                            .
                        </p>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="tw:fixed tw:top-0 tw:z-50 tw:flex tw:h-screen tw:w-screen tw:flex-col tw:items-center tw:justify-center tw:gap-4 tw:bg-white">
            <span className="tw:text-secondary">{errorMessage || "Redirecting to profile..."}</span>
            <Spinner />
        </div>
    );
};

Login.Layout = Layout;

export const getStaticProps: GetStaticProps<LoginProps> = () => ({
    props: {
        layout: {
            headerShadow: true,
            headerFluid: false,
            footerMode: "light",
        },
    },
});

export default Login;

import { useEffect } from "react";
import type { GetStaticProps, NextPage } from "next";
import SEO from "@components/seo/page-seo";
import { useRouter } from "next/router";
import Layout01 from "@layout/layout-01";
import Breadcrumb from "@components/breadcrumb";
import ProfileBio from "@containers/profile/bio";
import Spinner from "@ui/spinner";
import { useSession, signOut } from "next-auth/react";
import { useMount } from "@hooks";

type PageProps = {
    layout?: {
        headerShadow: boolean;
        headerFluid: boolean;
        footerMode: string;
    };
};

type PageWithLayout = NextPage<PageProps> & {
    Layout?: typeof Layout01;
};

const Profile: PageWithLayout = () => {
    const mounted = useMount();
    const { data: session, status } = useSession();
    const router = useRouter();

    useEffect(() => {
        if (status === "unauthenticated") {
            router.replace("/login").catch((error) => {
                console.error("Redirect error:", error);
            });
        }
    }, [status, router]);

    if (!mounted || status === "loading") {
        return (
            <div className="tw:fixed tw:top-0 tw:z-50 tw:flex tw:h-screen tw:w-screen tw:items-center tw:justify-center tw:bg-white">
                <Spinner />
            </div>
        );
    }

    if (!session) {
        return (
            <div className="tw:fixed tw:top-0 tw:z-50 tw:flex tw:h-screen tw:w-screen tw:items-center tw:justify-center tw:bg-white">
                <Spinner />
            </div>
        );
    }

    const handleLogout = async () => {
        try {
            await signOut({ redirect: false });
            await router.replace("/login");
        } catch (error) {
            console.error("Logout error:", error);
        }
    };

    return (
        <>
            <SEO title="Profile" />
            <Breadcrumb
                pages={[{ path: "/", label: "home" }]}
                currentPage="Profile"
                showTitle={false}
            />
            <ProfileBio />
            <div className="tw:mt-4 tw:flex tw:justify-center">
                <button
                    onClick={handleLogout}
                    type="button"
                    className="tw:rounded tw:bg-red-500 tw:px-4 tw:py-2 tw:text-white"
                >
                    Logout
                </button>
            </div>
        </>
    );
};

Profile.Layout = Layout01;

export const getStaticProps: GetStaticProps<PageProps> = () => {
    return {
        props: {
            layout: {
                headerShadow: true,
                headerFluid: false,
                footerMode: "light",
            },
        },
    };
};

export default Profile;

